import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { addSong } from '../services/favoriteSongsAPI';
import Loading from './Loading';

export default class MusicCard extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,

    };
  }

  toLocalStorage = (tracks) => {
    if (localStorage.getItem('music_card')) {
      return JSON.parse(localStorage.getItem('music_card'));
    } localStorage.setItem('music_card', JSON.stringify(tracks));
    return '';
  }

  getMusicsAll = () => {

    console.log(tracks);
    return this.toLocalStorage(tracks);
  }

  saveSong = async (music) => {
    this.toLocalStorage(this.getMusicsAll());
    this.setState({
      loading: true,
    });
    await addSong(music);
    this.setState(() => ({
      loading: false,
    }));
  }

  render() {
    const { loading } = this.state;
    const { musics } = this.props;
    const tracks = musics.map((music, index) => (
      index > 0 && (
        <section key={ index }>
          <span>Nome da m√∫sica:</span>
          <p>{ music.trackName }</p>
          <audio data-testid="audio-component" src={ music.previewUrl } controls>
            <track kind="captions" />
            <code>audio</code>
          </audio>
          <label
            htmlFor="favorite"
            data-testid={ `checkbox-music-${music.trackId}` }
          >
            Favorita
            <input
              type="checkbox"
              id="favorite"
              onChange={ () => this.saveSong(music) }
            />
          </label>
          <hr />
        </section>
      )));

    return (
      this.getMusicsAll()
    );
  }
}

MusicCard.propTypes = {
  musics: PropTypes.shape({
    map: PropTypes.string.isRequired,
  }).isRequired,
};
