import React, { Component } from 'react';

export default class Login extends Component {
  constructor() {
    super();
    this.state = {
      buttonDisable: true,
      login: '',
    };
  }

  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { buttonDisable, login } = this.state;
    return (
      <div data-testid="page-login">
        <header>
          Login
        </header>

        <br />

        <form>
          <label htmlFor="login" name="login">
            Nome de usu√°rio
            <br />
            <input
              type="text"
              name="login"
              data-testid="login-name-input"
              value={ login }
              onChange={ this.handleChange }
            />
          </label>

          <button
            type="submit"
            data-testid="login-submit-button"
            value={ buttonDisable }
            disabled
          >
            Entrar
          </button>
        </form>

      </div>
    );
  }
}
