import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import getMusics from '../services/musicsAPI';

export default class Album extends Component {
  constructor() {
    super();
    this.state = {
      musics: [],
    };
  }

  componentDidMount = async () => {
    const { match: { params: { id } } } = this.props;
    const musicsAlbum = await getMusics(id);
    const artists = musicsAlbum[0].artistName;
    const albumFront = musicsAlbum[0].artworkUrl100;
    this.setState({
      musics: musicsAlbum,
      artist: artists,
      disc: albumFront,
    });
    console.log(musicsAlbum);
  }

  render() {
    const { musics, artist, disc } = this.state;
    const hmtlMusics = musics.map((music, index) => (
      index < 1 && (
        <section>
          <h4
            data-testid="artist-name"
            key={ index }
          >
            { music.artistName }
          </h4>
          <span data-testid="album-name">
            { music.collectionName }
            { music.artistName }
          </span>
        </section>
      )));

    const htmlAlbum = (
      <section>
        <h3 data-testid="artist-name">
          { artist }
          <br />
          <img src={ disc } alt="album" />
        </h3>
        { hmtlMusics }
      </section>
    );

    return (
      <div data-testid="page-album">
        <Header />
        { htmlAlbum }
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string.isRequired,
    }).isRequired,
  }).isRequired,
};
