import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { addSong, getFavoriteSongs, removeSong } from '../services/favoriteSongsAPI';
import Loading from './Loading';

export default class MusicCard extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      fav: [],
    };
  }

  async componentDidMount() {
    const favSongs = await getFavoriteSongs();
    this.setState({
      fav: favSongs,
    });
  }

  saveSong = async (music) => {
    await addSong(music);
await getFavoriteSongs();
  }

  deletFavoriteSong = async (music) => removeSong(music)

  manageCheck = async (music, { target }) => {
    this.setState({
      loading: true,
    });
    console.log(target.checked);
    if (target.checked) {
      const favorite = await this.saveSong(music);
      this.setState(() => ({
        loading: false,
        fav: favorites,
      }));
    } else {
      const favorite = await this.deletFavoriteSong(music);
      this.setState(() => ({
        loading: false,
        fav: favorite,
      }));
    }
  };

  render() {
    const { loading, fav } = this.state;
    const { musics } = this.props;
    const tracks = musics.map((music, index) => (
      index > 0 && (
        <section key={ index }>
          <span>Nome da m√∫sica:</span>
          <p>{ music.trackName }</p>
          <audio data-testid="audio-component" src={ music.previewUrl } controls>
            <track kind="captions" />
            <code>audio</code>
          </audio>
          <label
            htmlFor={ music.trackId }
            data-testid={ `checkbox-music-${music.trackId}` }
          >
            Favorita
            <input
              type="checkbox"
              id={ music.trackId }
              onChange={ (e) => this.manageCheck(music, e) }
              checked={ fav.some((m) => m.trackId === music.trackId) }
            />
          </label>
          <hr />
        </section>
      )));

    return (
      loading ? <Loading /> : tracks
    );
  }
}

MusicCard.propTypes = {
  musics: PropTypes.shape({
    map: PropTypes.string.isRequired,
  }).isRequired,
};
