import React, { Component } from 'react';

export default class Login extends Component {
  constructor() {
    super();
    this.state = {
      buttonDisable: true,
      login: '',
    };
  }

  handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  }

  componentDidUpdate() {
    changeButtonState = () => {
      const { login } = this.state;
      const limitNumber = 3;
      if (login.length > limitNumber) {
        this.setState({
          buttonDisable: false,
        });
      }
      this.setState({
        buttonDisable: true,
      });
    };
  }

  buttonClick = ({ target }) => {
    target.preventDefault();
  }

  render() {
    const { buttonDisable, login } = this.state;
    return (
      <div data-testid="page-login">
        <header>
          Login
        </header>

        <br />

        <form>
          <label htmlFor="login" name="login">
            Nome de usu√°rio
            <br />
            <input
              type="text"
              name="login"
              value={ login }
              onChange={ this.handleChange }
              data-testid="login-name-input"
            />
          </label>

          <button
            type="submit"
            disabled={ buttonDisable }
            data-testid="login-submit-button"
            onClick={ this.buttonClick }
          >
            Entrar
          </button>
        </form>
      </div>
    );
  }
}
