import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Loading from '../components/Loading';
import Album from './Album';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

export default class Search extends Component {
  constructor() {
    super();
    this.state = {
      searched: '',
      buttonDisable: true,
      loading: false,
      albuns: [],
      apiResponse: false,
      prevSearched: '',
    };
  }

  getArtist = async () => {
    const { searched } = this.state;
    const typed = searched;
    this.setState({
      searched: '',
      loading: true,
      prevSearched: typed,
    });
    const artist = await searchAlbumsAPI(typed);
    this.setState(() => ({
      searched: '',
      loading: false,
      albuns: artist,
      apiResponse: true,
    }));
  };

  handleButton = () => {
    const { searched } = this.state;
    const bTOne = searched.length > 1;
    this.setState({
      buttonDisable: !bTOne,
    });
  }

  handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    }, () => {
      this.handleButton();
    });
  }

  getAlbuns = () => {
    const { albuns } = this.state;
    const htmlFinded = (
      <div className="container-albuns">
        { albuns.map((album) => (
          <ul
            className="albuns"
            key={ album.collectionId }
            data-testid={ `link-to-album-${album.collectionId}` }
            onClick={ <Link to={ `album/${album.collectionId}` }></Link> }
          >
            <img src={ album.artworkUrl100 } alt={ album.collectionName } />
            <li>{ album.collectionName }</li>
            <li>{ album.artistName }</li>
          </ul>
        ))}
      </div>
    );
    const hmtlNotFinded = (<p>Nenhum álbum foi encontrado</p>);
    return albuns.length > 0 ? htmlFinded : hmtlNotFinded;
  };

  render() {
    const {
      searched,
      buttonDisable,
      loading,
      apiResponse,
      prevSearched,
    } = this.state;

    const htmlSearch = (
      <form>
        <input
          name="searched"
          placeholder="Nome do Artista"
          type="text"
          data-testid="search-artist-input"
          value={ searched }
          onChange={ this.handleChange }
        />

        <button
          type="button"
          data-testid="search-artist-button"
          disabled={ buttonDisable }
          onClick={ this.getArtist }
        >
          Pesquisar
        </button>
      </form>
    );

    const htmlResultArtist = (
      <span>
        Resultado de álbuns de:
        {' '}
        { prevSearched }
      </span>
    );

    return (
      <div data-testid="page-search">
        <Header />
        { loading ? <Loading /> : htmlSearch }
        { apiResponse && htmlResultArtist }
        { apiResponse && this.getAlbuns() }
      </div>
    );
  }
}
